import React, { useEffect, useState } from 'react'
import jwt_decode from "jwt-decode";
import '../App.css';

const Home = (props) => {



  let [users, setUsers] = useState([]);

  const [message, setMessage] = useState('')

  let [currentUser, setCurrentUser] = useState(null)

  const [recipientName, setRecipientName] = useState('')
  let [userStartChat, setuserStartChat] = useState(null)
  let [chat, setChat] = useState(null)
  const [messages, setMessages] = useState([{}])

  function renderMessage(){
      let res = []
      for(let i = 0; i < messages.length; i++){
        res.push(<p>messages[0].username</p>)
      }
    return  res;
  }

  useEffect(() => {

    if (currentUser !== null && userStartChat !== null) {
      const timeout = setTimeout(getMessages, 15000);
      setInterval(timeout, 10000)
      document.getElementById('messages').appendChild(renderMessage());
    }
  });

  const getMessages = async (e) => {


    fetch("http://localhost:8080/api/user/".concat(currentUser.id).concat("/chat/").concat(userStartChat.id).concat("/message"), {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
        'Access-Control-Allow-Origin': '*'
      }
    }).then(response => response.json())
      .then((data) => {


        setMessages({ ...messages, ...data })

      })
      .catch((error) => {
        console.error(error);
      });

  }

  const submit1 = async (e) => {
    e.preventDefault();
    await fetch("http://localhost:8080/users", {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
        'Access-Control-Allow-Origin': '*'
      }
    }).then(response => response.json())
      .then((data) => {
        users = data
        console.log(users)
      })
      .catch((error) => {
        console.error(error);
      });
  }


  function onUsername(e) {
    setRecipientName(e.target.value)

  }

  function onMessage(e) {
    setMessage(e.target.value)

  }


  const findByUsername = (e) => {
    e.preventDefault();

    fetch("http://localhost:8080/users/byUsername?username=".concat(recipientName), {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
        'Access-Control-Allow-Origin': '*'
      }
    }).then(response => response.json())
      .then((data) => {
        setuserStartChat({ ...data, ...userStartChat })
        console.log(userStartChat)
      })
      .catch((error) => {
        console.error(error);
      });

    if (userStartChat !== null) {

      fetch("http://localhost:8080/users/current", {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
          'Access-Control-Allow-Origin': '*'
        }
      }).then(response => response.json())
        .then((data) => {
          setCurrentUser({ ...data, ...currentUser })
          console.log(currentUser)
        })
        .catch((error) => {
          console.error(error);
        });
            if(currentUser == null){
              fetch("http://localhost:8080/users/current", {
                method: 'GET',
                headers: {
                  'Content-Type': 'application/json',
                  'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
                  'Access-Control-Allow-Origin': '*'
                }
              }).then(response => response.json())
                .then((data) => {
                  setCurrentUser({ ...data, ...currentUser })
                  console.log(currentUser)
                })
                .catch((error) => {
                  console.error(error);
                });
            }

      fetch("http://localhost:8080/api/user/".concat(currentUser.id).concat("/chat/recipient/").concat(userStartChat.id), {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
          'Access-Control-Allow-Origin': '*'
        }
      }).then(response => response.json())
        .then((data) => {
          setChat({ ...data, ...chat })
          console.log(chat)
        })
        .catch((error) => {
          console.error(error);
        });

      if (chat === null)

        fetch("http://localhost:8080/api/user/".concat(currentUser.id).concat("/chat/add/").concat(userStartChat.id), {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
            'Access-Control-Allow-Origin': '*'
          }
        }).then(response => response.json())
          .then((data) => {
            setChat({ ...data, ...chat })
            console.log(chat)
          })
          .catch((error) => {
            console.error(error);
          });

    }
  }


  const sendMessage = async (e) => {
    let mess = {
      recipientId: userStartChat.id,
      senderId: currentUser.id,
      message: message
    }
    e.preventDefault();
    console.log(userStartChat)
    console.log(currentUser)
    await fetch("http://localhost:8080/api/user/".concat(currentUser.id).concat("/message/send"), {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
        'Access-Control-Allow-Origin': '*'
      },
      body: JSON.stringify(mess)
    }).then(response => response.json())
      .then((data) => {

        console.log(data)
      })
      .catch((error) => {
        console.error(error);
      });
    console.log(chat)
  }







  return (

    <div className="container">
      <div className="row mysettings-menu mysettings-find-button" >
        <form className="d-flex" onSubmit={findByUsername}>
          <input className="form-control me-2" type="search" placeholder="Recipient`s username" aria-label="Search" onChange={onUsername} name="username" />
          <button className="btn btn-outline-success" type="submit">Search</button>
        </form>
      </div>

      <div className="window-messages" id="messages">



      </div>

      <form onSubmit={sendMessage}>
        <div className="mysettings-send-message-button" >
          <input type="text" className="form-control" placeholder="Message" aria-label="Recipient's username" onChange={onMessage} aria-describedby="button-addon2" />
          <button className="btn btn-outline-secondary" type="submit" id="button-addon2" >Send</button>

        </div>
      </form>

    </div>

  )
}
export default Home;
