import React, { useEffect, useState } from 'react'

const Home = () => {

  let [users, setUsers] = useState([]);

  const [recipient, setRecipient] = useState('')


  const submit1 = async (e) => {
    e.preventDefault();
    await fetch("http://localhost:8080/users", {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
        'Access-Control-Allow-Origin': '*'
      }
    }).then(response => response.json())
      .then((data) => {
        users = data
        console.log(users)
      })
      .catch((error) => {
        console.error(error);
      });
  }


  function onUsername(e) {
    setRecipient(e.target.value)
    console.log(recipient)
  }


  const findByUsername = async (e) => {
    e.preventDefault();
    await fetch("http://localhost:8080/users/byUsername?username=".concat(recipient), {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
        'Access-Control-Allow-Origin': '*'
      }
    }).then(response => response.json())
      .then((data) => {
        console.log(data)
      })
      .catch((error) => {
        console.error(error);
      });
  }


  return (

    <div className="container">
      <div class="container-sm">100% шириной до контрольной точки small</div>
      <div class="container-md">100% шириной до контрольной точки medium</div>
      <div class="container-lg">100% шириной до контрольной точки large</div>
      <div class="container-xl">100% шириной до контрольной точки extra large</div>
      <div class="container-xxl">100% шириной до контрольной точки extra extra large</div>


    </div>

  )
}
export default Home;
