import React, { useEffect, useState } from 'react'
import jwt_decode from "jwt-decode";
import '../App.css';
import MessagesWindow from './MessagesWindow'

const Home = (props) => {



  let [users, setUsers] = useState([]);

  const [message, setMessage] = useState('')

  let [currentUser, setCurrentUser] = useState({ id: null, username: '' })

  const [recipientName, setRecipientName] = useState('')
  let [userStartChat, setuserStartChat] = useState({})
  let [chat, setChat] = useState({})
  const [messages, setMessages] = useState([])


setInterval(()=>{
  const button = document.getElementById('clickButton');
    button.form.submit();
},4000)

  function renderMessage() {
    return messages[0]
  }
  const getMessages = async (e) => {


    fetch("http://localhost:8080/api/user/".concat(currentUser.id).concat("/chat/").concat(userStartChat.id).concat("/message"), {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
        'Access-Control-Allow-Origin': '*'
      }
    }).then(response => response.json())
      .then((data) => {


        setMessages({ ...messages, ...data })

      })
      .catch((error) => {
        console.error(error);
      });

  }

  const submit1 = async (e) => {
    e.preventDefault();
    await fetch("http://localhost:8080/users", {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
        'Access-Control-Allow-Origin': '*'
      }
    }).then(response => response.json())
      .then((data) => {
        users = data
        console.log(users)
      })
      .catch((error) => {
        console.error(error);
      });
  }


  function onUsername(e) {
    setRecipientName(e.target.value)

  }

  function onMessage(e) {
    setMessage(e.target.value)

  }


  const findByUsername = async (e) => {
    e.preventDefault();

    const response = await fetch("http://localhost:8080/users/byUsername?username=".concat(recipientName), {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
        'Access-Control-Allow-Origin': '*'
      }
    })
    const userForStartChat = await response.json();

    setuserStartChat(userForStartChat)
    console.log(userStartChat);
    if (userStartChat !== null) {
      getCurrentUser()
      console.log(currentUser.id, userStartChat.id);
      if (checkChatOnExist(currentUser.id, userStartChat.id) !== 200){
        createChat()
        setMessages([])
      }

    }

  }


  const checkChatOnExist = async (recipientId, senderId) => {
    const response = await fetch("http://localhost:8080/api/user/".concat(senderId).concat("/chat/recipient/").concat(recipientId), {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
        'Access-Control-Allow-Origin': '*'
      }
    })
    const chatCheck = await response.json()
    if (response.status == 200) {

      setChat(chatCheck)
      console.log(chatCheck);
      setChat(chatCheck)
      return 200;
    }
    return 500

  }




  const getCurrentUser = async () => {

    const response = await fetch("http://localhost:8080/users/current",
      {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
          'Access-Control-Allow-Origin': '*'
        }
      })
    const cu = await response.json();
    console.log(cu);
    setCurrentUser(cu)

  }

  const createChat = async () => {
    const response = await fetch("http://localhost:8080/api/user/".concat(currentUser.id).concat("/chat/add/").concat(userStartChat.id),
      {
        method: 'POST',
        headers: {

          'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt'))

        }
      })
    const newChat = await response.json();
    setChat(newChat)

    console.log(newChat);

  }



  const sendMessage = async (e) => {
    let mess = {
      recipientId: userStartChat.id,
      senderId: currentUser.id,
      message: message
    }
    e.preventDefault();
    console.log(userStartChat)
    console.log(currentUser)
    await fetch("http://localhost:8080/api/user/".concat(currentUser.id).concat("/message/send"), {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
        'Access-Control-Allow-Origin': '*'
      },
      body: JSON.stringify(mess)
    }).then(response => response.json())
      .then((data) => {

        console.log(data)
      })
      .catch((error) => {
        console.error(error);
      });
    console.log(chat)
  }



  const getMessagesFromChat = async (e) => {
    e.preventDefault();
    if (currentUser.id !== undefined && userStartChat.id !== undefined) {
      const response = await fetch("http://localhost:8080/api/user/".concat(currentUser.id).concat("/chat/").concat(userStartChat.id).concat("/message"),
        {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
            'Access-Control-Allow-Origin': '*'
          }
        })

      const listOfMessages = await response.json()
      setMessages(listOfMessages)
      console.log(messages);

    }
  }




  return (

    <div className="container">
      <div className="row mysettings-menu mysettings-find-button" >
        <form className="d-flex" onSubmit={findByUsername}>
          <input className="form-control me-2" type="search" placeholder="Recipient`s username" aria-label="Search" onChange={onUsername} name="username" />
          <button className="btn btn-outline-success" type="submit">Search</button>
        </form>
      </div>

      <div>
        <form onClick={getMessagesFromChat} >
          <button type="submit" id="update-messages">GET</button>
        </form>
        {messages.map(m => <li key={m.id}>{m.message}  <b>{m.author}</b></li>)}
      </div>


      <form onSubmit={sendMessage}>
        <div className="mysettings-send-message-button" >
          <input type="text" className="form-control" placeholder="Message" aria-label="Recipient's username" onChange={onMessage} aria-describedby="button-addon2" />
          <button className="btn btn-outline-secondary" type="submit" id="button-addon2" >Send</button>

        </div>
      </form>

    </div>

  )
}
export default Home;
