import React, { useEffect, useState } from 'react'
import '../App.css';

const Home = () => {

  let [users, setUsers] = useState([]);

  const [recipient, setRecipient] = useState('')


  const submit1 = async (e) => {
    e.preventDefault();
    await fetch("http://localhost:8080/users", {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
        'Access-Control-Allow-Origin': '*'
      }
    }).then(response => response.json())
      .then((data) => {
        users = data
        console.log(users)
      })
      .catch((error) => {
        console.error(error);
      });
  }


  function onUsername(e) {
    setRecipient(e.target.value)
    console.log(recipient)
  }


  const findByUsername = async (e) => {
    e.preventDefault();
    await fetch("http://localhost:8080/users/byUsername?username=".concat(recipient), {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
        'Access-Control-Allow-Origin': '*'
      }
    }).then(response => response.json())
      .then((data) => {
        console.log(data)
      })
      .catch((error) => {
        console.error(error);
      });
  }


  return (

    <div className="container">
      <div className="row mysettings-menu" mysettings-menu>
        <form className="d-flex" onSubmit={findByUsername}>
          <input className="form-control me-2" type="search" placeholder="Username" aria-label="Search" onChange={onUsername} name="username" />
          <button className="btn btn-outline-success" type="submit">Search</button>
        </form>
      </div>


      <form>
        <div className="input-group mb-3" >
          <input type="text" className="form-control" placeholder="Message" aria-label="Recipient's username" aria-describedby="basic-addon2" />
          <div className="input-group-append">
            <button className="btn btn-outline-secondary" type="button">Send</button>
          </div>
        </div>
      </form>

    </div>

  )
}
export default Home;
