import React, { useEffect, useState } from 'react'

const Home = () => {

  let [users, setUsers] = useState([]);

  const [recipient, setRecipient] = useState('')


  const submit1 = async (e) => {
    e.preventDefault();
    await fetch("http://localhost:8080/users", {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
        'Access-Control-Allow-Origin': '*'
      }
    }).then(response => response.json())
      .then((data) => {
        users = data
        console.log(users)
      })
      .catch((error) => {
        console.error(error);
      });
  }


  function onUsername(e) {
    setRecipient(e.target.value)
    console.log(recipient)
  }


  const findByUsername = async (e) => {
    e.preventDefault();
    await fetch("http://localhost:8080/users/byUsername?username=".concat(recipient), {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
        'Access-Control-Allow-Origin': '*'
      }
    }).then(response => response.json())
      .then((data) => {
        console.log(data)
      })
      .catch((error) => {
        console.error(error);
      });
  }


  return (

    <div className="container">
      <div className="row">
        <div className="col">col</div>
        <div className="col"><form className="d-flex" onSubmit={findByUsername}>
          <input className="form-control me-2" type="search" placeholder="Username" aria-label="Search" onChange={onUsername} name="username" />
          <button className="btn btn-outline-success" type="submit">Search</button>
        </form></div>
        <div className="col">col</div>
        <div className="col">col</div>
      </div>
      <div className="row">
        <div className="col-8">col-8</div>
        <div className="col-4"><form>
          <div class="input-group mb-3">
            <input type="text" class="form-control" placeholder="Message" aria-label="Recipient's username" aria-describedby="basic-addon2" />
            <div class="input-group-append">
              <button class="btn btn-outline-secondary" type="button">Send</button>
            </div>
          </div>
        </form></div>
      </div>
    </div>

  )
}
export default Home;


