import React, { useEffect, useState } from 'react'
import '../App.css';

const Home = () => {

  let [users, setUsers] = useState([]);

  const [recipientName, setRecipientName] = useState('')
  const [userStartChat, setuserStartChat] = useState({})




  const submit1 = async (e) => {
    e.preventDefault();
    await fetch("http://localhost:8080/users", {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
        'Access-Control-Allow-Origin': '*'
      }
    }).then(response => response.json())
      .then((data) => {
        users = data
        console.log(users)
      })
      .catch((error) => {
        console.error(error);
      });
  }


  function onUsername(e) {
    setRecipientName(e.target.value)
    console.log(recipientName)
  }


  const findByUsername = async (e) => {
    e.preventDefault();
    await fetch("http://localhost:8080/users/byUsername?username=".concat(recipientName), {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '.concat(sessionStorage.getItem('jwt')),
        'Access-Control-Allow-Origin': '*'
      }
    }).then(response => response.json())
      .then((data) => {
        setuserStartChat(data)
        console.log(userStartChat)
      })
      .catch((error) => {
        console.error(error);
      });
  }


  return (

    <div className="container">
      <div className="row mysettings-menu mysettings-find-button" >
        <form className="d-flex" onSubmit={findByUsername}>
          <input className="form-control me-2" type="search" placeholder="Recipient`s username" aria-label="Search" onChange={onUsername} name="username" />
          <button className="btn btn-outline-success" type="submit">Search</button>
        </form>
      </div>

    <div className="window-messages">

    </div>

      <form>
        <div className="mysettings-send-message-button" >
          <input type="text" class="form-control" placeholder="Message" aria-label="Recipient's username" aria-describedby="button-addon2"/>
            <button class="btn btn-outline-secondary" type="button" id="button-addon2">Button</button>

        </div>
      </form>

    </div>

  )
}
export default Home;
